body.script:
// Estas configuraciones estan ligadas al config.php

// Paths app:
const path = "https://fixdate.io/";
const pathCountry = "https://fixdate.io/ar/";
const pathDomain = "";

const countryCode = "ar";

// Para manager
const pathLang = "_pathLang";
const langJsManager = "es";

// Consulto tipo de dispositivo
if (/Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent)) {

  var device = 'mobile';
  var pahWP = 'api';
} else {
  var device = 'desktop';
  var pahWP = 'web';
}

function support_format_webp() {
  var elem = document.createElement('canvas');

  if (!!(elem.getContext && elem.getContext('2d'))) {
    // was able or not to get WebP representation
    return elem.toDataURL('image/webp').indexOf('data:image/webp') == 0;
  } else {
    // very old browser like IE 8, canvas not supported
    return false;
  }
}

var prealoaderOption = $(window);

prealoaderOption.on("load", function() {

  var preloader = $('.loader');
  var preloaderArea = $('.preloader-area');

  preloader.fadeOut(300);
  preloaderArea.delay(100).fadeOut(300);

});

$('a.smoth-scroll').on("click", function(e) {
  var anchor = $(this);
  $('html, body').stop().animate({
    scrollTop: $(anchor.attr('href')).offset().top - 1
  }, 1000);
  e.preventDefault();
});

// PORTADA
var alto = $('header').height() * 2;
// console.log(alto);
$(window).scroll(function() {
  if ($(document).scrollTop() > alto) {
    $('.outer-menu').addClass('fixed');
    $('.whatsapp-fixed').addClass('whatsapp-fixed-right');
  }
  if ($(document).scrollTop() < alto) {
    $('.outer-menu').removeClass('fixed');
    $('.whatsapp-fixed').removeClass('whatsapp-fixed-right');
  }
});

$(document).ready(function() {


  // Recorro los links de wp
  $("a.whatsapp-link").each(function(index) {

    var wpMessage = $(this).attr('msj');
    var wpNumberto = $(this).attr('numberTo');

    // Cambio el path del link WP:
    $(this).attr("href", "https://" + pahWP + ".whatsapp.com/send?phone=" + wpNumberto + "&text=" + wpMessage);

  });


});

   $("#cbtn").click(function(e) {

     e.preventDefault();

     if (isokCSend()) //form bien validado
     {

       $("#cbtn").html(Lang_Form_Contacto_1);
       $("#cbtn").attr('disabled', true);


       // Obtengo el Form de contacto
       var formulario = $('#cform')[0];

       // Obtengo los datos del formulario
       var datos = new FormData(formulario);

       //  for (var pair of datos.entries()) {
       //    console.log(pair[0] + ', ' + pair[1]);
       //  }

       // Fetch para guardar la consulta y enviar notificacion.
       fetch(path + 'fetchs/send-form-contacto.php', {
           method: 'POST',
           body: datos
         })

         // Promesas fetch
         .then(res => res.json())
         .then(data => {

           // Si recibo error imprimo el nombre del error
           if (data.error === true) {

             $("#cerr").html('');
             $("#cform").after('
' + Lang_Form_Contacto_2 + '');
             $("#cbtn").html(Lang_Form_Contacto_9);
             $("#cbtn").removeAttr("disabled")
             $('#cform')[0].reset();

           }

           // Si no hay error
           if (data.error === false) {

             $("#cform").hide();
             $(".modal-body p.sub-title-form").hide();
             $("#modal-form-contacto .modal-header").hide();
             $("#modal-form-contacto .modal-footer").hide();
             $("#cbtn").html(Lang_Form_Contacto_9);
             $("#cbtn").removeAttr("disabled")
             $('#cform')[0].reset();
             $("#cform").after('
' + Lang_Form_Contacto_3 + '');

             // Cierro modal
             setTimeout(function() {
               $('#modal-form-contacto').modal('hide');
             }, 3000);

           }

         })

     }

   })



   // Validar contacto
   function isokCSend() {

     $("#cerr").html('');
     $("p.msj-error").hide();
     $("p.msj-error").html('');

     var flag = true;
     var err = '';
     var emailreg = /^([\w-\.]+@([\w-]+\.)+[\w-]{2,4})?$/;

     var cname = $.trim($("#cname").val());
     var cphone = $.trim($("#cphone").val());
     var cemail = $.trim($("#cemail").val());
     var cmessage = $.trim($("#cmessage").val());


     if (cname === '') {
       err = Lang_Form_Contacto_4;
       flag = false;
     }


     if (cphone === '') {
       err = Lang_Form_Contacto_5;
       flag = false;
     }


     if (cemail !== '') {
       if (!emailreg.test(cemail)) {
         err = Lang_Form_Contacto_6;
         flag = false;
       }
     } else {
       err = Lang_Form_Contacto_7;
       flag = false;
     }


     if (cmessage === '') {
       err = Lang_Form_Contacto_8;
       flag = false;
     }

     $("#cerr").html(err);

     return flag;

   }


   //  Reset al abrir el form de nuevo
   $('#modal-form-contacto').on('shown.bs.modal', function() {

     // Reset form
     $(".modal-body p.sub-title-form").show();
     $("#modal-form-contacto .modal-header").show();
     $("#modal-form-contacto .modal-footer").show();
     $("#cform").show();
     $("#cerr").html('');
     $("#modal-form-contacto .msj-contacto").remove();
     $("p.msj-error").hide();
     $("p.msj-error").html('');


   })

